document.readyState!=="loading"?scrollToc():document.addEventListener("DOMContentLoaded",function(){scrollToc()});function scrollToc(){const e=new IntersectionObserver(e=>{e.forEach(e=>{const t=e.target.getAttribute("id");e.intersectionRatio>0?document.querySelector(`nav ul li a[href="#${t}"]`).parentElement.classList.add("active"):document.querySelector(`nav ul li a[href="#${t}"]`).parentElement.classList.remove("active")})});document.querySelectorAll("h1[id],h2[id],h3[id]").forEach(t=>{e.observe(t)});function t(){document.querySelectorAll("nav ul li").forEach(e=>{e.classList.remove("active")})}}