document.readyState!=='loading'?scrollToc():document.addEventListener('DOMContentLoaded',function(){scrollToc()});function scrollToc(){const a=new IntersectionObserver(a=>{a.forEach(a=>{const b=a.target.getAttribute('id');a.intersectionRatio>0?document.querySelector(`nav ul li a[href="#${b}"]`).parentElement.classList.add('active'):document.querySelector(`nav ul li a[href="#${b}"]`).parentElement.classList.remove('active')})});document.querySelectorAll('h1[id],h2[id],h3[id]').forEach(b=>{a.observe(b)});function b(){document.querySelectorAll('nav ul li').forEach(a=>{a.classList.remove('active')})}}